<style>
  @import url("https://use.typekit.net/tfb5hou.css");

  :host {
    --footer-bg: #4b4b4b;
    --footer-fg: #fff;
  }

  .footer {
    background: var(--footer-bg);
    color: var(--footer-fg);
    padding-top: 1.25rem;
    padding-bottom: 2rem;
    width: 100%;
    font-family: "Arial", sans-serif;
  }

  .container {
    max-width: 920px;
    margin: auto;
  }

  /* two columns on desktop */
  .footer__main {
    display: grid;
    grid-template-columns: minmax(0, 1fr) max-content;
    /* left grows, right hugs */
    gap: 1rem 1rem;
  }

  .footer__brand {
    grid-column: 1 / -1;
    display: flex;
    align-items: center;
  }

  .footer__col {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: 1rem;
  }

  /*-- --------------------- Socials --------------------- --*/
  .footer__social {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    margin-top: 0.25rem;
    max-width: 162px;
  }

  /* make the icons inherit the link color */
  .footer__social a svg path {
    fill: currentColor !important;
    /* override inline fill=... */
    transition:
      fill 0.3s ease,
      opacity 0.3s ease;
  }

  /* animate the color on the link itself */
  .footer__social a {
    color: rgba(255, 255, 255, 0.8);
    /* default */
    transition:
      color 0.3s ease,
      transform 0.3s ease;
  }

  .footer__social a:hover {
    color: #fff;
    transform: translateY(-1px);
  }

  /*-- -------------------- Copyright -------------------- --*/
  .footer__copyright {
    font-size: 0.75rem;
    margin-top: 2rem;
  }

  .footer__copyright p {
    margin: 0;
    opacity: 0.6;
  }

  .footer__copyright a {
    color: #fff;
    transition: all 0.3s ease-in-out;
  }

  .footer__copyright a:hover {
    opacity: 0.8;
  }

  /*-- ------------------- Footer Text ------------------- --*/
  .footer__col.footer__csis {
    font-size: 0.9375rem;
    max-width: 688px;
  }

  .footer__col.footer__csis p {
    margin: 0;
  }

  .footer__col.footer__address {
    font-size: 0.875rem;
    max-width: 200px;
    line-height: 1.3;
  }

  .footer__col.footer__address p {
    margin: 0;
    color: rgba(255, 255, 255, 0.8);
  }

  /*-- --------------------------------------------------- --*/
  /*--                    Media Queries                    --*/
  /*-- --------------------------------------------------- --*/
  @media screen and (max-width: 1024px) {
    .footer {
      padding-top: 2rem;
      padding-bottom: 2rem;
    }

    .container {
      max-width: 506px;
    }

    .footer__col.footer__address {
      margin-left: 1rem;
    }

    .footer__copyright {
      margin-top: 2rem;
    }
  }

  @media screen and (max-width: 640px) {
    .footer {
      padding-top: 1.25rem;
      padding-bottom: 2rem;
    }

    .footer__main {
      grid-template-columns: 1fr;
    }

    .container {
      max-width: 320px;
    }

    .footer__col.footer__address {
      margin-top: 1rem;
      margin-bottom: 2rem;
    }
  }
</style>
